CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

CREATE TABLE "Campañas" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" character varying(200) NOT NULL,
    "FechaInicio" timestamp with time zone NOT NULL,
    "FechaFin" timestamp with time zone NOT NULL,
    "Presupuesto" numeric NOT NULL,
    "GastosEjecutados" numeric NOT NULL,
    "Estado" character varying(50) NOT NULL,
    "TipoCampaña" character varying(100) NOT NULL,
    "ZonaCobertura" text NOT NULL,
    "LeadsGenerados" integer NOT NULL,
    "VentasGeneradas" numeric NOT NULL,
    "ROI" numeric NOT NULL,
    "FechaCreacion" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_Campañas" PRIMARY KEY ("Id")
);

CREATE TABLE "Presupuestos" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Año" integer NOT NULL,
    "Mes" integer NOT NULL,
    "PresupuestoAsignado" numeric NOT NULL,
    "GastosEjecutados" numeric NOT NULL,
    "Departamento" text NOT NULL,
    "FechaActualizacion" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_Presupuestos" PRIMARY KEY ("Id")
);

CREATE TABLE "Leads" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" text NOT NULL,
    "Email" text NOT NULL,
    "Telefono" text NOT NULL,
    "Empresa" text NOT NULL,
    "Cargo" text NOT NULL,
    "Origen" text NOT NULL,
    "Estado" text NOT NULL,
    "CampañaId" integer NOT NULL,
    "FechaCreacion" timestamp with time zone NOT NULL,
    "Notas" text NOT NULL,
    CONSTRAINT "PK_Leads" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Leads_Campañas_CampañaId" FOREIGN KEY ("CampañaId") REFERENCES "Campañas" ("Id") ON DELETE RESTRICT
);

CREATE UNIQUE INDEX "IX_Campañas_Nombre" ON "Campañas" ("Nombre");

CREATE INDEX "IX_Leads_CampañaId" ON "Leads" ("CampañaId");

CREATE INDEX "IX_Leads_Email" ON "Leads" ("Email");

CREATE UNIQUE INDEX "IX_Presupuestos_Año_Mes_Departamento" ON "Presupuestos" ("Año", "Mes", "Departamento");

